import React ,{useEffect,useState}from 'react';
import { withStyles, makeStyles } from '@material-ui/core/styles';
import Table from '@material-ui/core/Table';
import TableBody from '@material-ui/core/TableBody';
import TableCell from '@material-ui/core/TableCell';
import TableContainer from '@material-ui/core/TableContainer';
import TableHead from '@material-ui/core/TableHead';
import CircularProgress from '@material-ui/core/CircularProgress';
import TableRow from '@material-ui/core/TableRow';
import Paper from '@material-ui/core/Paper';
import axios from "axios";
import TablePagination from '@material-ui/core/TablePagination';

import './Style/Table.css';
 
 
 

import {
  useParams,
} from "react-router-dom";

const StyledTableCell = withStyles((theme) => ({
  head: {
    backgroundColor: theme.palette.common.black,
    color: theme.palette.common.white,
  },
  body: {
    fontSize: 18,
    color:'black',
    fontFamily:'vardana',
      },
}))(TableCell);

const StyledTableRow = withStyles((theme) => ({
  root: {
    '&:nth-of-type(odd)': {
      backgroundColor: theme.palette.action.hover,
    },
  },
}))(TableRow);

const useStyles = makeStyles({
  table: {
    minWidth: 700,
  },
   root: {
    width: '100%',
  },
  container: {
    maxHeight: 440,
  },
});

export default function Tables({history,name}) {

  console.log("....../",name)

  const classes = useStyles();
  const [data,setdata]=useState([]);
  const [header,setheader]=useState([]);
 let {id} = useParams();
const [loading,setloading]=useState(false);
  const [page, setPage] = React.useState(0);
  const [rowsPerPage, setRowsPerPage] = React.useState(10);

  

 
   
  
const [prediction,setPrediction] = useState([]);

 

  const handleChangePage = (event, newPage) => {
    setPage(newPage);
  };

  const handleChangeRowsPerPage = (event) => {
    setRowsPerPage(+event.target.value);
    setPage(0);
  };

 

useEffect(function(){
   const headers={
    "Access-Control-Allow-Origin": "*",
   }
   setloading(true);
 axios.get(`http://localhost:9000/users/data/${name}`,{
        headers}).then(
        (res)=>{
          setdata(res.data.data[id]);

          let parr=[];
          Object.values(res.data.objdata).map((item,index)=>(
            parr.push(item)
          ))
          parr.push('#')
          parr.push('#')
          parr.push('#')
           setPrediction(parr);
 
          let arr=[];
          Object.values(res.data.data[id][0]).map((item,index) => (
             arr.push(item)
              ))
          setheader(arr);
          setloading(false);

      
        })
        .catch((err)=>{
          setloading(false);
        console.log("error");
        })


      },[history.location.pathname,name])    

 
 console.log("prediction data",prediction[0])
 

 
 

let arrays=[]
const showPrediction =()=>{
  for( var i=0;i<prediction.length();i++){
    if (prediction[i]==='I'){
      arrays.push('Increment')
    }
    else if(prediction[i]==="N"){
      arrays.push("Neutral")
    }
    else{
      arrays.push("Decrement")
    }

  }
}
showPrediction()

 
console.log("arrays:",arrays)


function replace(object){
  let newobj={};
   Object.values(object).map((item,index) => (
             newobj[header[index]]=item
              ))
  return newobj;
}


const finalans=[];
const rows = 
 header.length>0 ? data.slice(1).map((item) => 
        replace(item) )
:
[]
;
//console.log(".../////....",data);

  return (
      loading ? (
              <div
                style={{
                  height: '30vh',
                  display: 'flex',
                  width: '100%',
                  justifyContent: 'center',
                  alignItems: 'center',
                }}
              >
              <CircularProgress
              style={{width:"60px",height:"60px"}}
      />
              </div>
            ) : (
              <>

              <div>
                <table>
                  <tr>
                      {prediction.map((item,index)=>(
                             <td>
                                {item}
                            </td>
                          ))}
                  </tr>
                </table>
              
              </div>

    <TableContainer component={Paper}>
      <Table className={classes.table} aria-label="customized table">
        <TableHead>
          <TableRow>

            {header.length > 0 ? 
            <>
            {header.map((item,index) => (
  <StyledTableCell id={index}>{item}</StyledTableCell>
              ))}
            </> 
            :
            <>
            </>
             }
        
          </TableRow>
        </TableHead>
        <TableBody>
         {rows.slice(page * rowsPerPage, page * rowsPerPage + rowsPerPage).map((row,index) => 
               <StyledTableRow key={index}>
            { 
              header.map((i, subindex) =>
                   <StyledTableCell >{row[i]}</StyledTableCell>)
            }
 </StyledTableRow>
            )}
       </TableBody>
      </Table>
        <TablePagination
        rowsPerPageOptions={[10, 25, 100]}
        component="div"
        count={rows.length}
        rowsPerPage={rowsPerPage}
        page={page}
        onChangePage={handleChangePage}
        onChangeRowsPerPage={handleChangeRowsPerPage}
      />
    </TableContainer>
    </>
    )
  );
}
