import React ,{useEffect,useState}from 'react';
import { withStyles, makeStyles } from '@material-ui/core/styles';
import Table from '@material-ui/core/Table';
import TableBody from '@material-ui/core/TableBody';
import TableCell from '@material-ui/core/TableCell';
import TableContainer from '@material-ui/core/TableContainer';
import TableHead from '@material-ui/core/TableHead';
import CircularProgress from '@material-ui/core/CircularProgress';
import TableRow from '@material-ui/core/TableRow';
import Paper from '@material-ui/core/Paper';
import axios from "axios";
import TablePagination from '@material-ui/core/TablePagination';

import './Style/Table.css';
import './Style/PredictionTable.css';
 
 

import {
  useParams,
} from "react-router-dom";

const StyledTableCell = withStyles((theme) => ({
  head: {
    backgroundColor: theme.palette.common.black,
    color: theme.palette.common.white,
  },
  body: {
    fontSize: 18,
    color:'black',
    fontFamily:'vardana',
      },
}))(TableCell);

const StyledTableRow = withStyles((theme) => ({
  root: {
    '&:nth-of-type(odd)': {
      backgroundColor: theme.palette.action.hover,
    },
  },
}))(TableRow);

const useStyles = makeStyles({
  table: {
    minWidth: 700,
  },
   root: {
    width: '100%',
  },
  container: {
    maxHeight: 440,
  },
});

export default function Tables({history,name}) {

  console.log("....../",name)

  const classes = useStyles();
  const [data,setdata]=useState([]);
  const [header,setheader]=useState([]);
 let {id} = useParams();
const [loading,setloading]=useState(false);
  const [page, setPage] = React.useState(0);
  const [rowsPerPage, setRowsPerPage] = React.useState(10);

  

 
   
  
const [prediction,setPrediction] = useState([]);

 

  const handleChangePage = (event, newPage) => {
    setPage(newPage);
  };

  const handleChangeRowsPerPage = (event) => {
    setRowsPerPage(+event.target.value);
    setPage(0);
  };

 

useEffect(function(){
   const headers={
    "Access-Control-Allow-Origin": "*",
   }
   setloading(true);
 axios.get(`http://localhost:9000/users/data/${name}`,{
        headers}).then(
        (res)=>{
          setdata(res.data.data[id]);

          let parr=[];
          Object.values(res.data.objdata).map((item,index)=>(
            parr.push(item)
          ))
           
           setPrediction(parr);
 
          let arr=[];
          Object.values(res.data.data[id][0]).map((item,index) => (
             arr.push(item)
              ))
          setheader(arr);
          setloading(false);

      
        })
        .catch((err)=>{
          setloading(false);
        console.log("error");
        })


      },[history.location.pathname,name])    

 
 
 

 
let arrayCount=[]
var ci=0;
var cn=0;
var cd=0;
for(let i=0;i<prediction.length;i++){
  if (prediction[i]==='I')
    ci++
  if(prediction[i]==="D")
    cd++
  if(prediction[i]==="N")
    cn++
}
arrayCount.push(ci)
arrayCount.push(cd)
arrayCount.push(cn)

 
 // countind for final prediction

const showPrediction =(props)=>{
  if(name){
    if(props==='I'){
      ci=ci+1  // counting positive results

      return(
        <p style={{backgroundColor:'green',padding:"4px",color:'white'}}>Increment</p>
      )
    }
    else if (props==="D"){
      cd=cd+1
      return(
       <p style={{backgroundColor:'red',padding:"4px",color:'white'}}>Decrement</p>
      )
    }
    else{
      cn=cn+1
      return(
       <p style={{backgroundColor:'blue',padding:"4px 15px",color:'white'}}>Neutral</p>
      )
    }
  }
 else{
   return(
     <p></p>
   )
 }
}

//console.log(arrayCount)

const showFinalPredict = () =>{
  let x=Math.max(...arrayCount);
  if(x>=3){
    if(cn===cd ||cd===ci || cd===ci ){
      //neutral
      return(
        <p style={{backgroundColor:"#2D74E7",padding:"2px 20px ",color:'white'}}>Neutral</p>
      )
    }
   
  }
  if(x===cd){
    if(x<=3){
      //sell
      return(
        <p style={{backgroundColor:"#FF0E0E",padding:"2px 20px ",color:'white'}}> SELL</p>
      )
    }
    if(x>3){
      //strong sell
      return(
        <p style={{backgroundColor:'#DC0000',padding:"2px 20px",color:'white'}}> Strong SELL</p>
      )
    }
  }
  if(x===ci){
    if(x>=7){
      //strongest buy
      return(
        <p style={{backgroundColor:'#4E8109',padding:"2px 20px",color:'white'}}>Strongest BUY</p>
      )
      
    }
    if(x===6){
      //strong buy
      return(
        <p style={{backgroundColor:"#67A90E",padding:"2px 20px ",color:'white'}}>Strong BUY</p>
      )
    }
    if(x>=3){
      //buy
      return(
        <p style={{backgroundColor:"#90EA14",padding:"2px 20px ",color:'white'}}> BUY</p>
      )
    }

  }
  if(x===cn){
    return(
      //neutral
      <p style={{backgroundColor:"#2D74E7",padding:"2px 20px ",color:'white'}}>Neutral</p>
    )
  }
}

// const showFinalPredict = () =>{
//   if(name){
//     if (count>=7){
//       return(
//         <div>
//           <p style={{backgroundColor:'#4E8109',padding:"2px 20px",color:'white'}}>Strongest BUY</p>
//         </div>
//       )
//     }
//     else if(count==6){
//       return(
//         <div>
//           <p style={{backgroundColor:"#67A90E",padding:"2px 20px ",color:'white'}}>Strong BUY</p>
//         </div>
//       )
//     }
//     else if(count==5){
//       return(
//         <div>
//           <p style={{backgroundColor:"#90EA14",padding:"2px 20px ",color:'white'}}> BUY</p>
//         </div>
//       )
//     }
//     else if(count==4){
//       return(
//         <div>
//           <p style={{backgroundColor:"#2D74E7",padding:"2px 20px ",color:'white'}}>Neutral</p>
//         </div>
//       )
//     }
//     else if(count==3){
//       return(
//         <div>
//           <p style={{backgroundColor:"#FF0E0E",padding:"2px 20px ",color:'white'}}> SELL</p>
//         </div>
//       )
//     }
   
//     else{
//       return(
//         <div>
//           <p style={{backgroundColor:'#DC0000',padding:"2px 20px",color:'white'}}> Strong SELL</p>
//         </div>
//       )
//     }
//   }
   

// }
 


function replace(object){
  let newobj={};
   Object.values(object).map((item,index) => (
             newobj[header[index]]=item
              ))
  return newobj;
}


const finalans=[];
const rows = 
 header.length>0 ? data.slice(1).map((item) => 
        replace(item) )
:
[]
;
//console.log(".../////....",data);

  return (
      loading ? (
              <div
                style={{
                  height: '30vh',
                  display: 'flex',
                  width: '100%',
                  justifyContent: 'center',
                  alignItems: 'center',
                }}
              >
              <CircularProgress
              style={{width:"60px",height:"60px"}}
      />
              </div>
            ) : (
              <>

              <div className="prediction-table">
                <div>
                 
                  <table>
                  <tr>
                      {prediction.map((item,index)=>(
                             <td>
                                {showPrediction(item)}
                            </td>
                          ))}
                  </tr>
                </table>
                </div>
                
                <h3>PGR FInal Prediction:</h3>
                <h3>
                        {
                          showFinalPredict()
                        }
                </h3>
                        
              </div>

    <TableContainer component={Paper}>
      <Table className={classes.table} aria-label="customized table">
        <TableHead>
          <TableRow>

            {header.length > 0 ? 
            <>
            {header.map((item,index) => (
  <StyledTableCell id={index}>{item}</StyledTableCell>
              ))}
            </> 
            :
            <>
            </>
             }
        
          </TableRow>
        </TableHead>
        <TableBody>
         {rows.slice(page * rowsPerPage, page * rowsPerPage + rowsPerPage).map((row,index) => 
               <StyledTableRow key={index}>
            { 
              header.map((i, subindex) =>
                   <StyledTableCell >{row[i]}</StyledTableCell>)
            }
 </StyledTableRow>
            )}
       </TableBody>
      </Table>
        <TablePagination
        rowsPerPageOptions={[10, 25, 100]}
        component="div"
        count={rows.length}
        rowsPerPage={rowsPerPage}
        page={page}
        onChangePage={handleChangePage}
        onChangeRowsPerPage={handleChangeRowsPerPage}
      />
    </TableContainer>
    </>
    )
  );
}
