import React,{useState,useEffect} from 'react';
import './Style/Plans.css';

import CircularProgress from "@material-ui/core/CircularProgress";

const Plans = () => {

    const [plan,setPlan] =useState('1 Month Plan - $100');

    const Change =(e)=>{
        
        setPlan(e);
    }
 const [buyloading, setbuyloading] = useState(false);

async function displayRazorpay() {
    try {
      const res = await loadScript(
        "https://checkout.razorpay.com/v1/checkout.js"
      );

      if (!res) {
        alert("Razorpay SDK failed to load. Are you online?");
        return;
      }
      const token = localStorage.getItem("profile");
      setbuyloading(true);
      const response = await axios.post(
        `http://localhost:9000/users/createOrder`,
        {
          amount: plan,
        }
      );

      if (!response.data) {
        // alert('Server error. Are you online?');
        errorToast("Something went Wrong");
        setbuyloading(false);
        return;
      }

      // Getting the order details back
      const { amount, id: order_id, currency } = response.data;

      const options = {
        key: "rzp_test_jNJCAdggjvfKOc", // Enter the Key ID generated from the Dashboard
        amount,
        currency,
        name: "PGR Demo.",
        description: `Buy ${user.name}`,
        modal: {
          ondismiss: function () {
            setbuyloading(false);
          },
        },
        handler: async function (response) {
          const data = {
            orderCreationId: order_id,
            razorpayPaymentId: response.razorpay_payment_id,
            razorpayOrderId: response.razorpay_order_id,
            razorpaySignature: response.razorpay_signature,
            userId: user._id,
            amount,
          };
          // dispatch(actions.buyuserRequest(data));

          const resp = await axios.post('http://localhost:9000/users/buy', data);

          if (resp.status == 201) {
            successToast("Purchase successFull!");
          } else {
            errorToast("Couldnt buy user");
          }
          setbuyloading(false);
        },
        prefill: {
          name: "PGR Demo",
          email: "pgrdemo@example.com",
          contact: "9999999999",
        },
        notes: {
          address: "Praedico Global Research",
        },
        theme: {
          color: "#61dafb",
        },
      };

      const paymentObject = new window.Razorpay(options);
      paymentObject.open();
    } catch (error) {
      console.log(error, "sad");
      errorToast("Something Went Wrong Try Again!!");
      setbuyloading(false);
    }
  }

    return (
        <>
            <div className="card plans"  style={{width: '24rem',boxShadow:'0 2px 10px 4px gray'}}>
                <div className="card-img-top img"></div>
                    <div className="card-body">
                        <h5 className="card-title plans-title">Buy Our Plans</h5>
                        <p className="card-text plans-text">
                            <p>1 Month Plan - $100</p>
                            <p>3 Months Plan - $300</p>
                            <p>6 Months Plan - $600</p>
                            <p>12 Months Plan - $1000</p>
                        </p>
                        <label>Select Your Plan</label>
                        <div>
                            <select className="select-plan"   value={plan} onChange={e=>Change(e.target.value)}>
                                <option value="1 Month Plan - $100"    >1 Month Plan - $100</option>
                                <option value="3 Months Plan - $300"   >3 Months Plan - $300</option>
                                <option value="6 Months Plan - $600"   >6 Months Plan - $600</option>
                                <option value="12 Months Plan - $1000"   >12 Months Plan - $1000</option>                      
                            </select>                        
                              <Button
                                disabled={user?.purchased}
                              variant="contained" color="primary"
                  onClick={() => {
                    if (user) {
                      displayRazorpay();
                    } else {
                      history.replace("/login");
                    }
                  }}
                  style={{ cursor: "pointer" }}
                >
                  {buyloading ? (
                    <CircularProgress size={30} color="#fff" />
                  ) :
                    user?.purchased ? (
                    "Bought"
                  ) : (
                    "Buy Plan"
                  )
                  }
                </Button>
                        </div>
                    </div>
            </div>
        </>
            )
}

            export default Plans;
