import React ,{useEffect,useState}from 'react';
import { withStyles, makeStyles } from '@material-ui/core/styles';
import Table from '@material-ui/core/Table';
import TableBody from '@material-ui/core/TableBody';
import TableCell from '@material-ui/core/TableCell';
import TableContainer from '@material-ui/core/TableContainer';
import TableHead from '@material-ui/core/TableHead';
import CircularProgress from '@material-ui/core/CircularProgress';
import TableRow from '@material-ui/core/TableRow';
import Paper from '@material-ui/core/Paper';
import axios from "axios";
import TablePagination from '@material-ui/core/TablePagination';
import VisibilityIcon from '@material-ui/icons/Visibility';
import Popover from '@material-ui/core/Popover';
import Typography from '@material-ui/core/Typography';
import Button from '@material-ui/core/Button';
import {Link,useHistory,useLocation} from 'react-router-dom'
import './Style/Table.css';
import './Style/PredictionTable.css';

 

import {
  useParams,
} from "react-router-dom";

const StyledTableCell = withStyles((theme) => ({
  head: {
    backgroundColor: theme.palette.common.black,
    color: theme.palette.common.white,
  },
  body: {
    fontSize: 18,
    color:'black',
    fontFamily:'vardana',
      },
}))(TableCell);

const StyledTableRow = withStyles((theme) => ({
  root: {
    '&:nth-of-type(odd)': {
      backgroundColor: theme.palette.action.hover,
    },
  },
}))(TableRow);

const useStyles = makeStyles((theme)=>({
  table: {
    minWidth: 700,
  },
   root: {
    width: '100%',
  },
  container: {
    maxHeight: 440,
  },
  typography: {
    padding: theme.spacing(2),
  },
}));

export default function Tables({history,name}) {

  console.log("....../",name)

  const classes = useStyles();
  const [data,setdata]=useState([]);
  const [header,setheader]=useState([]);
 let {id} = useParams();
const [loading,setloading]=useState(false);
  const [page, setPage] = React.useState(0);
  const [rowsPerPage, setRowsPerPage] = React.useState(10);

  const [user,setUser]=useState(JSON.parse(localStorage.getItem('profile')));
  console.log("------------",user)

  useEffect(()=>{
    const token=user?.token
    setUser(JSON.parse(localStorage.getItem('profile')))
  })


  const [anchorEl, setAnchorEl] = React.useState(null);

  const handleClick = (event) => {
    setAnchorEl(event.currentTarget);
  };

  const handleClose = () => {
    setAnchorEl(null);
  };

  const open = Boolean(anchorEl);
  const idd = open ? 'simple-popover' : undefined;
   
  
const [prediction,setPrediction] = useState([]);

 

  const handleChangePage = (event, newPage) => {
    setPage(newPage);
  };

  const handleChangeRowsPerPage = (event) => {
    setRowsPerPage(+event.target.value);
    setPage(0);
  };

 

useEffect(function(){
   const headers={
    "Access-Control-Allow-Origin": "*",
   }
   setloading(true);
 axios.get(`http://localhost:9000/users/data/${name}`,{
        headers}).then(
        (res)=>{
          setdata(res.data.data[id]);

          let parr=[];
          Object.values(res.data.objdata).map((item,index)=>(
            parr.push(item)
          ))
           
           setPrediction(parr);
 
          let arr=[];
          Object.values(res.data.data[id][0]).map((item,index) => (
             arr.push(item)
              ))
          setheader(arr);
          setloading(false);

      
        })
        .catch((err)=>{
          setloading(false);
        console.log("error");
        })


      },[history.location.pathname,name])    

 
 
 

 
let arrayCount=[]
var ci=0;
var cn=0;
var cd=0;
for(let i=0;i<prediction.length;i++){
  if (prediction[i]==='I')
    ci++
  if(prediction[i]==="D")
    cd++
  if(prediction[i]==="N")
    cn++
}
arrayCount.push(ci)
arrayCount.push(cd)
arrayCount.push(cn)

 
 // countind for final prediction

const showPrediction =(props)=>{
  if(name){
    if(props==='I'){
      //ci=ci+1  // counting positive results

      return(
        <td style={{backgroundColor:'#4cd137',color:'white'}}>
          Increment
        </td>
      )
    }
    else if (props==="D"){
      //cd=cd+1
      return(
        <td style={{backgroundColor:'#e84118',color:'white'}}>
        Decrement
      </td>
      )
    }
    else{
      //cn=cn+1
      return(
        <td style={{backgroundColor:'#00a8ff',color:'white'}}>
        Neutral
      </td>
      )
    }
  }
 else{
   return(
     <p></p>
   )
 }
}

//console.log(arrayCount)

const showFinalPredict = () =>{
  let x=Math.max(...arrayCount);
  if(name){
  if(x>=3){
    if(cd===ci){
      //neutral
      return(
        <p style={{backgroundColor:"#2D74E7",padding:"7px 20px ",color:'white',borderRadius:'5px'}}>Neutral</p>
      )
    }
   
  }
  if(x===cd){
    if(x===3){
      //sell
      return(
        <div>
          <p color={{backgroundColor:"#2ecc71",padding:"7px 20px ",color:'white',borderRadius:'5px'}}>Strong BUY</p>
        </div>
      )
    }
    if(x>3){
      //strong sell
      return(
        <p style={{backgroundColor:'#DC0000',padding:"7px 20px ",color:'white',borderRadius:'5px'}}> Strong SELL</p>
      )
    }
  }
  if(x===ci){
    if(x>=7){
      //strongest buy
      return(
        <p style={{backgroundColor:'#4E8109',padding:"7px 20px ",color:'white',borderRadius:'5px'}}>Strongest BUY</p>
      )
      
    }
    if(x===6){
      //strong buy
      return(
