import React,{useState} from 'react'
import {getUserListforPayment} from './Nodeservices'
import { Paper, makeStyles, TableBody, TableRow, TableCell, Toolbar, InputAdornment } from '@material-ui/core';
import useTable from './UseTable'
import { Search } from "@material-ui/icons";
import { TextField } from '@material-ui/core';
import Typography from '@material-ui/core/Typography';
import Switch from '@material-ui/core/Switch';
import Grid from '@material-ui/core/Grid';
import CheckCircleIcon from '@material-ui/icons/CheckCircle';
const useStyles = makeStyles(theme => ({
    pageContent: {
        margin: theme.spacing(5),
        padding: theme.spacing(3)
    },
    searchInput: {
        width: '75%'
    }
}))


const headCells = [
    { id: 'name', label: 'Name' },
    { id: 'email', label: 'Email Address' },
    { id: 'mobile', label: 'Mobile No.' },
    { id: 'payment', label: 'Payment Status'},
]

const PaymentUserList=()=>{

    const [state, setState] = React.useState({
		checkedA: false,
		checkedB: true,
	  });

	  const [flag,setFlag]=React.useState(true)

	  const handleChange2 = (event) => {
		  if(flag==false){
			setFlag(true)
		  }
		  else{
			setFlag(false)
		  }
	  };
	  console.log(flag)

    const classes = useStyles();
    const [getList1,setList1]=React.useState([])
    const [getList2,setList2]=React.useState([])

    const fetchData=async()=>{

      var result=await getUserListforPayment('userlistforpayment')
      var result2=await getUserListforPayment('userlistforpayment2')

      setList1(result)
      setList2(result2)
      console.log(result)
    }
    React.useEffect(function(){
      fetchData()

    },[])

    const [filterFn, setFilterFn] = useState({ fn: items => { return items; } })

    const [filterFn2, setFilterFn2] = useState({ fn: items2 => { return items2; } })

    const {
        TblContainer,
        TblHead,
        TblPagination,
        recordsAfterPagingAndSorting
    } = useTable(getList1, headCells, filterFn);

    const {
        TblContainer2,
        TblHead2,
        TblPagination2,
        recordsAfterPagingAndSorting2
    } = useTable(getList2, headCells, filterFn2);

    const handleSearch = e => {
        let target = e.target;
        setFilterFn({
            fn: items => {
                if (target.value == "")
                    return items;
                else
                    return items.filter(x => x.name.toLowerCase().includes(target.value))
            }
        })
    }

    console.log(getList1)

    return(<>
    <div>
        <center>
            <h1>User Payment List</h1>
        </center>
    </div>
    
    <center>
				<Typography component="div">
					<center>
					<Grid component="label" container alignItems="center" spacing={1}>
					<Grid item><b>Subscribed Users</b></Grid>
						<Grid item>
							<Switch checked={flag?state.checkedA:state.checkedB} value={flag} onChange={handleChange2}/>
						</Grid>
					<Grid item><b>Non Subscriber Users</b></Grid>
					</Grid>
					</center>
				</Typography>
	</center>

        {flag?
        <>
        <div>
        
        <Paper className={classes.pageContent}>
            
            <Toolbar>
                <TextField
                    label="Search Users"
                    className={classes.searchInput}
                    InputProps={{
                        startAdornment: (<InputAdornment position="start">
                            <Search />
                        </InputAdornment>)
                    }}
                    onChange={handleSearch}
                />
            </Toolbar>
            <TblContainer>
                <TblHead />
                <TableBody>
                    {
                        recordsAfterPagingAndSorting().map(item =>
                            (<TableRow key={item.id}>
                                <TableCell><b>{item.name}</b></TableCell>
                                <TableCell><b>{item.email}</b></TableCell>
                                <TableCell><b>{item.mobile}</b></TableCell>
                                <TableCell><b><CheckCircleIcon style={{color:'#2ecc71'}}/>{item.adminpayment ? <>Done by User</>:<>Done by Admin</>}</b></TableCell>
                            </TableRow>)
                        )
                    }
                </TableBody>
            </TblContainer>
            <TblPagination />
        </Paper>
        
    
    </div>
    </>
        :
        <>
        <div>
        
        <Paper className={classes.pageContent}>
            
            <Toolbar>
                <TextField
                    label="Search Users"
                    className={classes.searchInput}
                    InputProps={{
                        startAdornment: (<InputAdornment position="start">
                            <Search />
                        </InputAdornment>)
                    }}
                    onChange={handleSearch}
                />
            </Toolbar>
            <TblContainer2>
                <TblHead2 />
                <TableBody>
                    {
                        recordsAfterPagingAndSorting2().map(item =>
                            (<TableRow key={item.id}>
                                <TableCell><b>{item2.name}</b></TableCell>
                                <TableCell><b>{item2.email}</b></TableCell>
                                <TableCell><b>{item2.mobile}</b></TableCell>
                                <TableCell><b>{item2.payment.toString()}</b></TableCell>
                            </TableRow>)
                        )
                    }
                </TableBody>
            </TblContainer2>
            <TblPagination2 />
        </Paper>
        
    
    </div>
        </>
        }

    
    </>)
    
}

export default PaymentUserList
