import React,{useState,useLayoutEffect} from 'react';
import PropTypes from 'prop-types';
import LoyaltyIcon from '@material-ui/icons/Loyalty';
import Addbatch from "./addbatch";
import { makeStyles } from '@material-ui/core/styles';
import DashboardIcon from '@material-ui/icons/Dashboard';
import TreeView from '@material-ui/lab/TreeView';
import TreeItem from '@material-ui/lab/TreeItem';
import Typography from '@material-ui/core/Typography';
import ExitToAppIcon from '@material-ui/icons/ExitToApp';
import AddCircleOutlineIcon from '@material-ui/icons/AddCircleOutline';
import Label from '@material-ui/icons/ShoppingCartOutlined';
import Topic from "./addtopic";
import Showbatch from "./displaybatch";
import Displaytopic from "./displaytopic";
import SupervisorAccountIcon from '@material-ui/icons/SupervisorAccount';
import Arrow from '@material-ui/icons/PlayArrowOutlined';
import ArrowDropDownIcon from '@material-ui/icons/ArrowDropDown';
import ArrowRightIcon from '@material-ui/icons/ArrowRight';
import VpnKeyIcon from '@material-ui/icons/VpnKey';
import Register from './Register';
import Userlist from "./userlist";
import Coursechange from "./coursechange";
import  Spinner from "../../spinner";
import Divider from '@material-ui/core/Divider';
import UserModal from "./userModal";
import DisplayFormat from "./DisplayFormat";
import MenuBookIcon from '@material-ui/icons/MenuBook';
import CategoryInterface from "./CategoryInterface";
//& ${classname write } to change
const useTreeItemStyles = makeStyles((theme) => ({
  root: {
      color: theme.palette.text.secondary,
      '&:hover > $content': {
        backgroundColor: theme.palette.action.hover,
      },
      '&:focus > $content, &$selected > $content': {
       backgroundColor: `var(--tree-view-bg-color, ${theme.palette.grey[400]})`,
        color: 'var(--tree-view-color)',
      },
      '&:focus > $content $label, &:hover > $content $label, &$selected > $content $label': {
        backgroundColor: 'transparent',
      },
    },
    content: {
      color: theme.palette.text.secondary,
      borderTopRightRadius: theme.spacing(2),
      borderBottomRightRadius: theme.spacing(2),
      paddingRight: theme.spacing(1),
      fontWeight: theme.typography.fontWeightMedium,
      '$expanded > &': {
        fontWeight: theme.typography.fontWeightRegular,
      },
    },
    group: {
      marginLeft: 0,
      '& $content': {
        paddingLeft: theme.spacing(2),
      },
    },
    expanded: {},
    selected: {},
    label: {
      fontWeight: 'inherit',
      color: 'inherit',
    },
    labelRoot: {
      display: 'flex',
      alignItems: 'center',
      padding: theme.spacing(1, 0),
    },
    labelIcon: {
      marginRight: theme.spacing(3),
      fontSize: "29px",
    },

    labelText: {
      fontWeight: 'inherit',
      flexGrow: 1,
    }


  }));
  
  function StyledTreeItem(props) {
    const classes = useTreeItemStyles();
    const { labelText, labelIcon: LabelIcon, labelInfo, color, bgColor, ...other } = props;
  
    return (
      <TreeItem
        label={
          <div className={classes.labelRoot}>
            <LabelIcon color="inherit" className={classes.labelIcon} />
            <Typography variant="body2" className={classes.labelText}>
              {labelText}
            </Typography>
            <Typography variant="caption" color="inherit">
              {labelInfo}
            </Typography>
          </div>
        }
        style={{
          '--tree-view-color': color,
          '--tree-view-bg-color': bgColor,
        }}
        classes={{
          root: classes.root,
          content: classes.content,
          expanded: classes.expanded,
          selected: classes.selected,
          group: classes.group,
          label: classes.label,
        }}
        {...other}
      />
    );
  }
  
  StyledTreeItem.propTypes = {
    bgColor: PropTypes.string,
    color: PropTypes.string,
    labelIcon: PropTypes.elementType.isRequired,
    labelInfo: PropTypes.string,
    labelText: PropTypes.string.isRequired,
  };
  
  const useStyles = makeStyles({
    root: {
      height: 264,
      flexGrow: 1,
      maxWidth: 400,
    },
  });
 
export default function DashboardList(props)  {
 // const [width,setWidth]=useState(0)
    const classes = useStyles();  
    const [size, setSize] = useState(0);

const handleClick=(value)=>{

props.handleView(value)
{size <450 ?  props.handleDrawerClose() :  props.handleDrawer();}
}
const updateSize = () =>{
  setSize(window.innerWidth);
} 
useLayoutEffect(() => {
        
  window.addEventListener('resize', updateSize);
  updateSize();
  return () => window.removeEventListener('resize', updateSize);
}, []);
 return(   
    <TreeView
    className={classes.root}
    defaultCollapseIcon={<ArrowDropDownIcon />}
    defaultExpandIcon={<ArrowRightIcon />}
    defaultEndIcon={<div style={{ width: 24 }} />}
    defaultSelected= {['5']}
    defaultExpanded= {['100']}
  > 
     <StyledTreeItem nodeId="100" labelText="Register" labelIcon={DashboardIcon}>
      <StyledTreeItem
        nodeId="5"
        labelText="ADD User"
        labelIcon={VpnKeyIcon}
        onClick={()=>handleClick(<Register />)} 
        color="#1a73e8"
        bgColor="#e8f0fe"
      />
      <StyledTreeItem
        nodeId="6"
        labelText="Display"
        labelIcon={Arrow}
        onClick={()=>handleClick(<Userlist />)} 
        color="#e3742f"
        bgColor="#fcefe3"
      />
        <StyledTreeItem
        nodeId="7"
        labelText="Course Change"
        labelIcon={Arrow}
        onClick={()=>handleClick(<Coursechange />)} 
        color="#e3742f"
        bgColor="#fcefe3"
      />
       </StyledTreeItem> 
       <StyledTreeItem
          nodeId="300"
          labelText="Edit Details"
          labelIcon={SupervisorAccountIcon}
          
        onClick={()=>handleClick(<UserModal />)}
        color="#a250f5"
        bgColor="#f3e8fd"
        /> 
       <StyledTreeItem nodeId="400" labelText="Courses" labelIcon={MenuBookIcon}>
      <StyledTreeItem
        nodeId="9"
        labelText="Add Course"
        labelIcon={Arrow}
        onClick={()=>handleClick(<CategoryInterface />)} 
         
        color="#1a73e8"
        bgColor="#e8f0fe"
      />
      <StyledTreeItem
        nodeId="10"
        labelText="Display All"
        labelIcon={Arrow}
        onClick={()=>handleClick(<DisplayFormat />)}  
        color="#e3742f"
        bgColor="#fcefe3"
      />
       </StyledTreeItem>
       <StyledTreeItem nodeId="600" labelText="Batch" labelIcon={LoyaltyIcon}>
      <StyledTreeItem
        nodeId="13"
        labelText="Add Batch"
        labelIcon={Arrow}
        onClick={()=>handleClick(<Addbatch />)} 
         
        color="#337d54"
        bgColor="#7cd9a5"
      />
      <StyledTreeItem
        nodeId="14"
        labelText="Display All"
        labelIcon={Arrow}
        onClick={()=>handleClick(<Showbatch />)}  
        color="#337d54"
        bgColor="#7cd9a5"
      />
       </StyledTreeItem>
       <StyledTreeItem nodeId="500" labelText="Add Topics" labelIcon={AddCircleOutlineIcon}>
      <StyledTreeItem
        nodeId="11"
        labelText="Add Material"
        labelIcon={Arrow}
        onClick={()=>handleClick(<Topic />)} 
       
        color="#1a73e8"
        bgColor="#e8f0fe"
      />
      <StyledTreeItem
        nodeId="12"
        labelText="Display All"
        labelIcon={Arrow}
        onClick={()=>handleClick(<Displaytopic />)} 
       
        color="#e3742f"
        bgColor="#fcefe3"
      />
       </StyledTreeItem>
      {/*   
       <StyledTreeItem nodeId="200" labelText="XYZ" labelIcon={Label}>
      <StyledTreeItem
        nodeId="8"
        labelText="Add"
        labelIcon={VpnKeyIcon}
        onClick={()=>handleClick(<Spinner/>)} 
        color="#1a73e8"
        bgColor="#e8f0fe"
      />
      <StyledTreeItem
        nodeId="15"
        labelText="Display"
        labelIcon={Arrow}
        onClick={()=>handleClick(<Spinner />)} 
        color="#e3742f"
        bgColor="#fcefe3"
      />
       </StyledTreeItem> 
    */}
    <Divider />
    <Divider />
       <StyledTreeItem
        nodeId="50"
        labelText="Logout"
        labelIcon={ExitToAppIcon}
        onClick={()=>handleClick(50)} 
        color="#1a73e8"
        bgColor="#e8f0fe"
      />

  </TreeView>
);
 }
