import React ,{useEffect,useState}from 'react';
import { withStyles, makeStyles } from '@material-ui/core/styles';
import Table from '@material-ui/core/Table';
import TableBody from '@material-ui/core/TableBody';
import TableCell from '@material-ui/core/TableCell';
import TableContainer from '@material-ui/core/TableContainer';
import TableHead from '@material-ui/core/TableHead';
import CircularProgress from '@material-ui/core/CircularProgress';
import TableRow from '@material-ui/core/TableRow';
import Paper from '@material-ui/core/Paper';
import axios from "axios";
import TablePagination from '@material-ui/core/TablePagination';

import './Style/Table.css';

 
 

import {
  useParams,
} from "react-router-dom";

const StyledTableCell = withStyles((theme) => ({
  head: {
    backgroundColor: theme.palette.common.black,
    color: theme.palette.common.white,
  },
  body: {
    fontSize: 18,
    color:'black',
    fontFamily:'vardana',
      },
}))(TableCell);

const StyledTableRow = withStyles((theme) => ({
  root: {
    '&:nth-of-type(odd)': {
      backgroundColor: theme.palette.action.hover,
    },
  },
}))(TableRow);

const useStyles = makeStyles({
  table: {
    minWidth: 700,
  },
   root: {
    width: '100%',
  },
  container: {
    maxHeight: 440,
  },
});

export default function Tables({history,name}) {

  console.log(name)

  const classes = useStyles();
  const [data,setdata]=useState([]);
  const [header,setheader]=useState([]);
 let {id} = useParams();
const [loading,setloading]=useState(false);
  const [page, setPage] = React.useState(0);
  const [rowsPerPage, setRowsPerPage] = React.useState(10);

  


  //handling Pridictions states
  const [count,setCount] = useState(0);  //for strong strongest Neutral buy

  const [Ppsar,setPpsar] = useState({
    current:'',
    previous:''

  });
  const [Padx,setPadx] = useState({
    adx:'',
    plusDi:'',
    minsDi:''

  });
  const [Prsi,setPrsi] = useState({
    current:'',
    previous:''

  });
   
  


 

  const handleChangePage = (event, newPage) => {
    setPage(newPage);
  };

  const handleChangeRowsPerPage = (event) => {
    setRowsPerPage(+event.target.value);
    setPage(0);
  };

 

useEffect(function(){
   const headers={
    "Access-Control-Allow-Origin": "*",
   }
   setloading(true);
 axios.get(`http://localhost:9000/users/data/${name}`,{
        headers}).then(
        (res)=>{
          setdata(res.data.data[id]);

          


          //handling setprediction state through /:id
          if(id=='psar'){
            setPpsar({
              current:res.data.data[id][1][1],
              previous:res.data.data[id][2][1]
            })
          }
          if(id=='adx'){
            setPadx({
              adx:res.data.data[id][1][2],
              plusDi:res.data.data[id][1][3],
              minusDi:res.data.data[id][1][4],
            })
          }
          if(id=='rsi'){
            setPrsi({
              current:res.data.data[id][1][2],
              previous:res.data.data[id][2][2],
            })
          }
          
          




          let arr=[];
          Object.values(res.data.data[id][0]).map((item,index) => (
             arr.push(item)
              ))
          setheader(arr);
          setloading(false);

      
        })
        .catch((err)=>{
          setloading(false);
        console.log("error");
        })


      },[history.location.pathname,name])    

console.log('THis is psar pr',Prsi);
 




// method:: handling predictions  of individual section ex:psar adx

const pre =()=>{
  if(id=='psar'){
    if(Ppsar.current>Ppsar.previous){

      count=count+1  // for each postive status 
        return(
          <div>
            <p style={{color:'green'}}>Increment </p>
          </div>
        )
      
    }
    else{
      return(
        <div>
          <p style={{color:"red"}} >Decrement</p>
        </div>
      )
    }
}
}




//return method for final production count 
const FinalPrediction =()=>{
  //handling only for 6 sections of table upto  :: Bolinger Bands
  if(count>=6){
    return(
      <>
      <p style={{color:'green'}} >Strongest BUY </p>
      </>
    )
  }
  if(count>=4){
    return(
      <>
      <p style={{color:'green'}} >Strong BUY </p>
      </>
    )
  }
  else{
    return(
      <>
      <p style={{color:'green'}} >Moderate BUY </p>
      </>
    )
  }
}




function replace(object){
  let newobj={};
   Object.values(object).map((item,index) => (
             newobj[header[index]]=item
              ))
  return newobj;
}


const finalans=[];
const rows = 
 header.length>0 ? data.slice(1).map((item) => 
        replace(item) )
:
[]
;
console.log(data);

  return (
      loading ? (
              <div
                style={{
                  height: '30vh',
                  display: 'flex',
                  width: '100%',
                  justifyContent: 'center',
                  alignItems: 'center',
                }}
              >
              <CircularProgress
              style={{width:"60px",height:"60px"}}
      />
              </div>
            ) : (
              <>




 {/* PGR PREDICTION TABLE REPRESENTATION OF DATA */}
        <div>
            
            <div>
                <h5>
                  PGR Prediction:
                </h5>
                {pre()}  {/* Prediction method calling */}
            </div>
            <br></br>
            <hr></hr>
            <br></br>
            
            <div>
                <h5>
                   PGR Overall Prediction:
                </h5>
                {FinalPrediction()} {/* Final Prediction method calling */}
            </div>
            <br></br>
           
          
        </div>




    <TableContainer component={Paper}>
      <Table className={classes.table} aria-label="customized table">
        <TableHead>
          <TableRow>

            {header.length > 0 ? 
            <>
            {header.map((item,index) => (
  <StyledTableCell id={index}>{item}</StyledTableCell>
              ))}
            </> 
            :
            <>
            </>
             }
        
          </TableRow>
        </TableHead>
        <TableBody>
         {rows.slice(page * rowsPerPage, page * rowsPerPage + rowsPerPage).map((row,index) => 
               <StyledTableRow key={index}>
            { 
              header.map((i, subindex) =>
                   <StyledTableCell >{row[i]}</StyledTableCell>)
            }
 </StyledTableRow>
            )}
       </TableBody>
      </Table>
        <TablePagination
        rowsPerPageOptions={[10, 25, 100]}
        component="div"
        count={rows.length}
        rowsPerPage={rowsPerPage}
        page={page}
        onChangePage={handleChangePage}
        onChangeRowsPerPage={handleChangeRowsPerPage}
      />
    </TableContainer>
    </>
    )
  );
}
